    def draw(self, screen, mapnr, tilesize, player, minions):
        for y in range(len(self.maps[mapnr])):
            for x in range(len(self.maps[mapnr][0])):
                self.xy = [tilesize*x-player.x+416, tilesize*y]
                if self.maps[mapnr][y][x] == 1:
                    pygame.draw.rect(screen, [100, 180, 60], [self.xy[0], self.xy[1], tilesize, tilesize])
                    screen.blit(self.grass, self.xy)
                if self.maps[mapnr][y][x] == 2:
                    screen.blit(self.dirt, self.xy)
                if self.maps[mapnr][y][x] == 3:
                    minions.append(Minion(tilesize*x, self.xy[1], 3, tilesize, 0))
                    self.maps[mapnr][y][x] = 0
                if self.maps[mapnr][y][x] == 4:
                    minions.append(Minion(tilesize*x, tilesize*y, 3, tilesize, 1))
                    self.maps[mapnr][y][x] = 0
                if self.maps[mapnr][y][x] == 5:
                    pygame.draw.rect(screen, [50, 50, 50], [self.xy[0], self.xy[1], tilesize, tilesize])
